  MetEx = {'ade','ala__L','ala__D','akg','arg__L','arg__D','asn__L',...
     'asn__D','asp__L','asp__D','btn','fol','glu__L','glu__D',...
     'gln__L','gln__D','gly','leu__L','leu__D','ile__L','met__L',...
     'met__D','pnto__R','phe__L','phe__D','purine','pyrimid',...
     'ribflv','ser__L','ser__D','thr__L','thym','trp__L','ura'};

%MetEx = {'A','B'};
Glucose = {'glc__D'};
%Glucose = {'Glu'};
% Find location inside the models for each organisms
Met_loc = find(ismember(Com.mets, [strcat(MetEx,'_c_org1') strcat(MetEx,'_c_org2')]));
Met_loc_e = find(ismember(Com.mets, [strcat(MetEx,'_e_org1') strcat(MetEx,'_e_org2')]));
%% Add new metabolites and rxns associated with the shared fraction
Com.S(end+1:end+size(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),1),:) = 0;
Com.mets(end+1:end+size(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),1)) = ...
    strrep(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),'_c','_s');
    % Add new rxns associated with transport in this new compartment S plus
    % drain
     Com.S(:,end+1:end+size(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),1)*3) = 0;
        Com.rxns(end+1:end+size(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),1)) =...
        strcat(upper(strrep(strrep(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),'_c','_s'),'_s','')),'tr_s_org1');
        Com.rxns(end+1:end+size(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),1)) =...
        strcat(upper(strrep(strrep(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),'_c','_s'),'_s','')),'tr_s_org2');
        Com.rxns(end+1:end+size(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),1)) =...
        strcat('EX_',strrep(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),'_c','_s'));
    
    % Create genes
        Com.genes(end+1:end+size(unique(strrep(strrep(Com.mets(Met_loc),'_org2',''),'_org1','')),1)) = cellstr(strcat('shared',string(1:size(unique(strrep(strrep(Com.mets(Met_loc(:,1)),'_org2',''),'_org1','')),1))));
        Com.grRules(end+1:size(Com.rxns,1)) = {'sharedtemp'};
for i = 1:size(Met_loc,1) % link location of transport reaction to _c and _s and drain
    Met_loc(i,2) = find(ismember(Com.rxns,strrep(upper(Com.mets(Met_loc(i))),'_C_ORG','tr_s_org')));
    Met_loc(i,3) = find(ismember(Com.mets,strrep(strrep(strrep(Com.mets(Met_loc(i)),'_org2',''),'_org1',''),'_c','_s')));
    Met_loc(i,4) = find(ismember(Com.rxns,strcat('EX_',strrep(unique(strrep(strrep(Com.mets(Met_loc(i)),'_org2',''),'_org1','')),'_c','_s'))));
    Met_loc(i,5) = Met_loc(i,4)-(size(Com.S,2)-size(unique(strrep(strrep(Com.mets(Met_loc(:,1)),'_org2',''),'_org1','')),1));
    Com.S(Met_loc(i,1),Met_loc(i,2)) = -1;
    Com.S(Met_loc(i,3),Met_loc(i,2)) = 1;
    Com.S(Met_loc(i,3),Met_loc(i,4)) = -1;
    Com.grRules(Met_loc(i,2)) = strrep(Com.grRules(Met_loc(i,2)),'temp',num2str(Met_loc(i,5)));
%     Com.grRules(Met_loc(i,4)) = strrep(Com.grRules(Met_loc(i,4)),'temp',num2str(find(ismember(Com.genes,strcat('shared',num2str(Met_loc(i,5)))))));
    Com.grRules(Met_loc(i,4)) = {''};
end

% Adjust the rest of the model parameters
Com.lb(end+1:size(Com.S,2)) = 0;
Com.lb(find(contains(Com.rxns,'tr_s_'))) = -100;
Com.ub(end+1:size(Com.S,2)) = 100;
% Com.ub(Met_loc(:,4)) = 0;
Com.rxnNames(end+1:size(Com.S,2)) = {'shared'};
Com.c(end+1:size(Com.S,2)) = 0;
Com.rev(end+1:size(Com.S,2)) = 1;

Com.metNames(end+1:size(Com.S,1)) = unique(strrep(strrep(Com.metNames(Met_loc(:,1)),'_org2',''),'_org1',''));
Com.metFormulas(end+1:size(Com.S,1)) = {'S'};
Com.metCharge(end+1:size(Com.S,1)) = 0;
Com.b(end+1:size(Com.S,1)) = 0;
Com.lb(find(contains(Com.rxns,'ATPM'))) = 0; % remove ATPM

%% Make them compete for glucose
% First there is not only one glucose sink and one glucose met called
% glc__D_com
    Com.S(find(contains(Com.mets,strcat(Glucose,'_e_org1'))),:) = ...
    Com.S(find(contains(Com.mets,strcat(Glucose,'_e_org1'))),:)+Com.S(find(contains(Com.mets,strcat(Glucose,'_e_org2'))),:);
    Com.S(find(contains(Com.mets,strcat(Glucose,'_e_org2'))),:) = 0; % old glucose dont exist
    Com.rxns(find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org1')))) = strcat(strcat('EX_',Glucose),'_e_com');
    Com.mets(find(contains(Com.mets,strcat(Glucose,'_e_org1')))) = strrep(Com.mets(find(contains(Com.mets,strcat(Glucose,'_e_org1')))),'_org1','_com');
% Delete the redundant met and rxns (save space)
    Com.S(find(contains(Com.mets,strcat(Glucose,'_e_org2'))),:) = [];
    Com.metNames(find(contains(Com.mets,strcat(Glucose,'_e_org2')))) = [];
    Com.metCharge(find(contains(Com.mets,strcat(Glucose,'_e_org2')))) = [];
    Com.metFormulas(find(contains(Com.mets,strcat(Glucose,'_e_org2')))) = [];
    Com.b(find(contains(Com.mets,strcat(Glucose,'_e_org2')))) = [];
    Com.mets(find(contains(Com.mets,strcat(Glucose,'_e_org2')))) = [];

    Com.S(:,find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org2')))) = [];
    Com.grRules(find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org2')))) = [];
    Com.rxnNames(find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org2')))) = [];
    Com.lb(find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org2')))) = [];
    Com.ub(find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org2')))) = [];
    Com.c(find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org2')))) = [];
    Com.rev(find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org2')))) = [];
    Com.rxns(find(contains(Com.rxns,strcat(strcat('EX_',Glucose),'_e_org2')))) = [];
%% Finalize
% Test model
FBA=cplexlp(-Com.c,[],[],Com.S,Com.b,Com.lb,Com.ub);
Biomass = FBA(find(ismember(Com.c,1)))

% Save
save ('MCS_Com_iAF_PP.mat','Com')  